    <script type="text/javascript" async>
        function generateAjaxRequest(apiAction, requestData, consumer) {
            $.ajax({
                url: 'https://intern.cyzetlc.eu/rest/api.php',
                type: 'POST',
                // Form data
                data: function() {
                    var data = new FormData();
                    for (let i = 0; i < requestData.length; i++) {
                        const strData = requestData[i].split(":");
                        data.append(strData[0], strData[1]);
                        console.log(strData[0] + ":" + strData[1])
                    }
                    data.append("action", apiAction);
                    return data;
                }(),
                success: function(data) {
                    consumer(data);
                },
                error: function(data) {
                    console.log(data);
                },
                complete: function() {

                },
                cache: false,
                contentType: false,
                processData: false
            });

        }
    </script>
